import React from "react";
import Checkbox from './Checkbox';
import Panda from '../assets/panda.png';
import './Login.css';
import { useNavigate } from 'react-router-dom';

function Login() {
    const navigate = useNavigate();

    const handleLogin = () => {

        navigate('/Home');
    };

    const handleSignUp = () => {

        navigate('/');
    };

    return (
        <div className="Login">
            <div className="login-left" style={{marginLeft:'200px',width:'300px'}}>

                <div className="info">
                    <h2 className="SigninHeading">Welcome to ShopNow</h2>
                    <div className="SigninPara">
                        It is the one-stop destination for all your shopping needs.<br />
                        Explore all the exciting offers and discounts on our products.
                    </div>
                </div>

                <div className="LoginSpace">
                    <div className="inputBox">
                        <input type="text" placeholder="Enter your email" className="inputField" />
                        <input type="password" placeholder="Enter your password" className="inputField" />
                    </div>
                    <div style={{marginBottom:"20px"}}>
                        <button className="LoginBtn" onClick={handleLogin}>Login</button>
                        <button className="SignUpBtn" onClick={handleSignUp}>Sign Up</button>
                    </div>
                </div>

                <Checkbox />
            </div>
            <div className="login-right">
                <img src={Panda} alt="Panda" style={{margin:"200px"}} />
            </div>
        </div>
    );
}

export default Login;
