import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaHeart, FaUser, FaShoppingCart, FaSearch, FaMinus, FaPlus } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import './Electronics.css'; // Make sure this path matches your CSS file's location

function Electronics() {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/products/');
        setProducts(response.data.filter(product => product.category === 'electronics')); // Assuming 'category' field indicates the category
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const [cartItems, setCartItems] = useState({});
  const handleCartAction = (productName, action = 'add') => {
    setCartItems(prevItems => {
      const newQuantity = action === 'add' ? (prevItems[productName] || 0) + 1 : Math.max(0, (prevItems[productName] || 1) - 1);
      if (newQuantity > 0) {
        return { ...prevItems, [productName]: newQuantity };
      } else {
        const { [productName]: remove, ...rest } = prevItems;
        return rest;
      }
    });
  };

  const renderCartButton = (productName) => {
    const count = cartItems[productName] || 0;
    return count > 0 ? (
      <div className="quantity-controls">
        <button onClick={() => handleCartAction(productName, 'subtract')}><FaMinus /></button>
        <span>{count}</span>
        <button onClick={() => handleCartAction(productName, 'add')}><FaPlus /></button>
      </div>
    ) : (
      <button className="add-to-cart-button" onClick={() => handleCartAction(productName, 'add')}>
        Add to Cart <FaShoppingCart />
      </button>
    );
  };

  const categories = {
    phone: 'Phones',
    laptop: 'Laptops',
    accessory: 'Accessories',
  };

  return (
    <div className="Electronics">
      {/* Header, Navigation, and other components remain the same */}
      <main className="Electronics-main">
        {Object.entries(categories).map(([key, title]) => (
          <section className="Electronics-section" key={key}>
            <h2>{title}</h2>
            <div className="product-grid">
              {products.filter(product => product.what === key).map((product) => (
                <div className="product-card" key={product.name}>
                  <img src={product.url} alt={product.name} />
                  <h3>{product.name}</h3>
                  <div style={{display: 'flex', marginTop: "10px"}}>
                    <p>${product.price}</p>
                    {renderCartButton(product.name)}
                  </div>
                </div>
              ))}
            </div>
          </section>
        ))}
      </main>
      {/* Footer remains unchanged */}
    </div>
  );
}

export default Electronics;
