import React, { useState } from "react";
import './Login.css'; 
import Panda from '../assets/panda.png'; 
import { useNavigate } from 'react-router-dom';
import { FaEye, FaEyeSlash } from 'react-icons/fa';
import axios from "axios";

function Login() {
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const [formData, setFormData] = useState({
        email: "",
        password: "",
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleLogin = async () => {
        try {
            const response = await axios.post("http://localhost:8000/api/check_user/", formData);
            if (response.data.exists) {
                console.log('Login successful');
                // Store CSRF token in local storage
                localStorage.setItem('csrfToken', response.headers['x-csrf-token']);
                console.log('CSRF token:', response.headers['x-csrf-token']);
                navigate('/Home');
            } else {
                alert('Incorrect credentials. Please try again.');
            }
        } catch (error) {
            console.error("Error logging in:", error);
            alert('Login failed. Please try again later.');
        }
    };

    const handleSignUp = () => {
        navigate('/Signup');
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    return (
        <div className="Login">
            <div className="login-left">
                <div className="info">
                    <h2 className="SigninHeading">Welcome to ShopNow</h2>
                    <div className="SigninPara">
                        It is the one-stop destination for all your shopping needs.<br />
                        Explore all the exciting offers and discounts on our products.
                    </div>
                </div>

                <div className="LoginSpace">
                    <div className="inputBox">
                        <input type="text" name="email" placeholder="Enter your email" className="inputField" onChange={handleInputChange} />
                        <div className="passwordInput">
                            <input type={showPassword ? "text" : "password"} name="password" placeholder="Enter your password" className="inputField" onChange={handleInputChange} />
                            <span onClick={togglePasswordVisibility} className="passwordToggleIcon">
                                {showPassword ? <FaEyeSlash /> : <FaEye />}
                            </span>
                        </div>
                    </div>
                    <div className="loginButtons">
                        <button className="LoginBtn" onClick={handleLogin}>Login</button>
                        <button className="SignUpBtn" onClick={handleSignUp}>Sign Up</button>
                    </div>
                </div>
            </div>
            <div className="login-right">
                <img src={Panda} alt="Panda" />
            </div>
        </div>
    );
}

export default Login;
