import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Payment.css';

function Payment() {
  const navigate = useNavigate();
  const [paymentMode, setPaymentMode] = useState('');
  const [totalQuantity, setTotalQuantity] = useState(0);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect(() => {
    const fetchTotalQuantity = async () => {
      try {

        const userEmail = localStorage.getItem('user_email');
        const response = await axios.post('http://localhost:8000/api/get_product_info', {
          user_email: userEmail,
        });
        setTotalQuantity(response.data.total_quantity);
      } catch (error) {
        console.error('Error fetching total quantity:', error);
      }
    };

    const fetchTotalAmount = async () => {
      try {
        const csrfToken = document.querySelector('[name=csrfmiddlewaretoken]').value;
        const userEmail = localStorage.getItem('user_email');
        const response = await axios.post('http://localhost:8000/api/get_cart_total_amount/', {
          user_email: userEmail,
        }, {
          headers: {
            'X-CSRFToken': csrfToken
          }
        });
        setTotalAmount(response.data.total_amount);
      } catch (error) {
        console.error('Error fetching total amount:', error);
      }
    };

    fetchTotalQuantity();
    fetchTotalAmount();
  }, []);

  const handlePayment = async () => {
    if (!paymentMode) {
      alert('Please select a payment mode.');
      return;
    }

    try {
      const csrfToken = document.querySelector('[name=csrfmiddlewaretoken]').value;
      const userEmail = localStorage.getItem('user_email');
      const response = await axios.post('http://localhost:8000/api/complete_payment/', {
        user_email: userEmail,
        total_amount: totalAmount,
        payment_mode: paymentMode,
      }, {
        headers: {
          'X-CSRFToken': csrfToken
        }
      });
      if (response.data.message) {
        alert('Payment successful!');
        navigate('/Home');
      }
    } catch (error) {
      console.error('Error processing payment:', error);
    }
  };

  const handleCancelPayment = () => {
    navigate('/Cart');
  };

  return (
    <div className="Payment">
      <h1>Payment</h1>
      <div className="payment-details">
        <div className="total-quantity">Total Quantity: {totalQuantity}</div>
        <div className="total-amount">Total Amount: ${totalAmount}</div>
        <div className="payment-modes">
          <label>
            <input
              type="radio"
              value="Card"
              checked={paymentMode === 'Card'}
              onChange={() => setPaymentMode('Card')}
            />
            Card
          </label>
          <label>
            <input
              type="radio"
              value="Cash"
              checked={paymentMode === 'Cash'}
              onChange={() => setPaymentMode('Cash')}
            />
            Cash
          </label>
          <label>
            <input
              type="radio"
              value="UPI"
              checked={paymentMode === 'UPI'}
              onChange={() => setPaymentMode('UPI')}
            />
            UPI
          </label>
        </div>
        <div className="payment-buttons">
          <button onClick={handlePayment}>Pay</button>
          <button onClick={handleCancelPayment}>Cancel Payment</button>
        </div>
      </div>
    </div>
  );
}

export default Payment;
