import React, { useState } from 'react';
import axios from 'axios';
import './AdminHome.css';

function AdminHome() {
    const [imageUrl, setImageUrl] = useState('');
    const [itemName, setItemName] = useState('');
    const [itemPrice, setItemPrice] = useState('');
    const [itemQuantity, setItemQuantity] = useState('');
    const [itemType, setItemType] = useState('');
    const [itemWhat, setItemWhat] = useState('');

    const handleAddItem = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/add_item', {
                url: imageUrl,
                name: itemName,
                price: itemPrice,
                quantity: itemQuantity,
                item_type: itemType,
                what: itemWhat,
            });
            console.log(response.data); // Assuming backend returns a success message
            // Optionally, you can show a success message or reset the form fields here
            setImageUrl('');
            setItemName('');
            setItemPrice('');
            setItemQuantity('');
            setItemType('');
            setItemWhat('');
        } catch (error) {
            console.error('Error adding item:', error);
            // Optionally, you can show an error message here
        }
    };

    const handleSearch = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/get_product_info', {
                product_id: productId,
                category: category,
            });
            setProductDetails(response.data);
        } catch (error) {
            console.error('Error fetching product details:', error);
        }
    };

    return (
        <div className="AdminHome">
            <h1>Add New Item</h1>
            <div className="add-item-form">
                <input
                    type="text"
                    placeholder="Image Url"
                    value={imageUrl}
                    onChange={(e) => setImageUrl(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Item Name"
                    value={itemName}
                    onChange={(e) => setItemName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Price"
                    value={itemPrice}
                    onChange={(e) => setItemPrice(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Quantity"
                    value={itemQuantity}
                    onChange={(e) => setItemQuantity(e.target.value)}
                />
                <select value={itemType} onChange={(e) => setItemType(e.target.value)}>
                    <option value="">Select Category</option>
                    <option value="electronics">Electronics</option>
                    <option value="clothing">Clothing</option>
                    <option value="decorations">Decorations</option>
                </select>
                <input
                    type="text"
                    placeholder="What's this?"
                    value={itemWhat}
                    onChange={(e) => setItemWhat(e.target.value)}
                />
                <button onClick={handleAddItem}>Add Item</button>
            </div>
        </div>
    );
}

export default AdminHome;
