import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Payment.css';

function Payment({ totalAmount }) {
  const navigate = useNavigate();
  const [paymentMode, setPaymentMode] = useState('');

  const handlePayment = async () => {
    if (!paymentMode) {
      alert('Please select a payment mode.');
      return;
    }

    try {
      const userEmail = localStorage.getItem('user_email');
      const response = await axios.post('http://localhost:8000/api/complete_payment/', {
        user_email: userEmail,
        total_amount: totalAmount,
        payment_mode: paymentMode,
      });
      if (response.data.message) {
        alert('Payment successful!');
        navigate('/Home');
      }
    } catch (error) {
      console.error('Error processing payment:', error);
    }
  };

  const handleCancelPayment = () => {
    navigate('/Cart');
  };

  return (
    <div className="Payment">
      <h1>Payment</h1>
      <div className="payment-details">
        <div className="total-amount">Total Amount: ${totalAmount}</div>
        <div className="payment-modes">
          <label>
            <input
              type="radio"
              value="Card"
              checked={paymentMode === 'Card'}
              onChange={() => setPaymentMode('Card')}
            />
            Card
          </label>
          <label>
            <input
              type="radio"
              value="Cash"
              checked={paymentMode === 'Cash'}
              onChange={() => setPaymentMode('Cash')}
            />
            Cash
          </label>
          <label>
            <input
              type="radio"
              value="UPI"
              checked={paymentMode === 'UPI'}
              onChange={() => setPaymentMode('UPI')}
            />
            UPI
          </label>
        </div>
        <div className="payment-buttons">
          <button onClick={handlePayment}>Pay</button>
          <button onClick={handleCancelPayment}>Cancel Payment</button>
        </div>
      </div>
    </div>
  );
}

export default Payment;
