import React, { useState } from 'react';
import './Clothing.css';
import { FaHeart, FaUser, FaShoppingCart, FaSearch, FaMinus, FaPlus } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

function Clothing() {
    const navigate = useNavigate();

    // Initialize state for cart items and their quantities
    const [cartItems, setCartItems] = useState({});

    // Functions to modify cart items
    const addToCart = (productName) => {
        setCartItems((prevItems) => ({
            ...prevItems,
            [productName]: (prevItems[productName] || 0) + 1,
        }));
    };

    const increaseQuantity = (productName) => {
        setCartItems((prevItems) => ({
            ...prevItems,
            [productName]: prevItems[productName] + 1,
        }));
    };

    const decreaseQuantity = (productName) => {
        setCartItems((prevItems) => ({
            ...prevItems,
            [productName]: Math.max(0, prevItems[productName] - 1),
        }));
    };

    // Helper function to render the add to cart or quantity buttons
    const renderCartButton = (productName) => {
        const count = cartItems[productName] || 0;
        return count > 0 ? (
            <div className="quantity-controls">
                <button onClick={() => decreaseQuantity(productName)}><FaMinus /></button>
                <span>{count}</span>
                <button onClick={() => increaseQuantity(productName)}><FaPlus /></button>
            </div>
        ) : (
            <button className="add-to-cart-button" onClick={() => addToCart(productName)}>
                Add to Cart <FaShoppingCart />
            </button>
        );
    };

    return (
        <div className="Clothing">
            {/* Header and Navigation */}
            <main className="Clothing-main">
                {/* New Arrivals Section */}
                <section className="Clothing-section">
                    <h2>New Arrivals</h2>
                    <div className="product-grid">
                        {/* Repeat this structure for each product */}
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1597633135469-5b71b07845ad" alt="Floral Dress" />
                            <h3>Floral Dress</h3>
                            <p>$120</p>
                            {renderCartButton('Floral Dress')}
                        </div>
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1618354691373-d851c5c3a990" alt="Casual Shirt" />
                            <h3>Casual Shirt</h3>
                            <p>$70</p>
                            {renderCartButton('Casual Shirt')}
                        </div>
                    </div>
                </section>
                {/* Women's Clothing Section */}
                <section className="Clothing-section">
                    <h2>Women's Clothing</h2>
                    <div className="product-grid">
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1523362628745-0c100150b36e" alt="Summer Blouse" />
                            <h3>Summer Blouse</h3>
                            <p>$50</p>
                            {renderCartButton('Summer Blouse')}
                        </div>
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1515669097368-22e68427d265" alt="Evening Dress" />
                            <h3>Evening Dress</h3>
                            <p>$150</p>
                            {renderCartButton('Evening Dress')}
                        </div>
                    </div>
                </section>
                {/* Men's Clothing Section */}
                <section className="Clothing-section">
                    <h2>Men's Clothing</h2>
                    <div className="product-grid">
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1588359348347-9bc6cbbb689e" alt="Leather Jacket" />
                            <h3>Leather Jacket</h3>
                            <p>$200</p>
                            {renderCartButton('Leather Jacket')}
                        </div>
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1602810318383-e386cc2a3ccf" alt="Sports Jacket" />
                            <h3>Sports Jacket</h3>
                            <p>$120</p>
                            {renderCartButton('Sports Jacket')}
                            </div>
                    </div>
                </section>
                {/* Kids' Clothing Section */}
                <section className="Clothing-section">
                    <h2>Kids' Clothing</h2>
                    <div className="product-grid">
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1519098901909-b1553a76f5e8" alt="Kids' Casual Wear" />
                            <h3>Kids' Casual Wear</h3>
                            <p>$30</p>
                            {renderCartButton('Kids Casual Wear')}
                        </div>
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1471286174890-9c112ffca5b4" alt="Kids' Formal Wear" />
                            <h3>Kids' Formal Wear</h3>
                            <p>$45</p>
                            {renderCartButton('Kids Formal Wear')}
                        </div>
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1609940730301-5f3bfdc8fb8e" alt="Kids' Sportswear" />
                            <h3>Kids' Sportswear</h3>
                            <p>$25</p>
                            {renderCartButton('Kids Sportswear')}
                        </div>
                        <div className="product-card">
                            <img src="https://images.unsplash.com/photo-1579423301087-5f8d1c30b2e3" alt="Kids' Pajamas" />
                            <h3>Kids' Pajamas</h3>
                            <p>$20</p>
                            {renderCartButton('Kids Pajamas')}
                        </div>
                    </div>
                </section>
            </main>
            <footer className="Clothing-footer">
                <p>&copy; ShopNow - Built With Love</p>
            </footer>
        </div>
    );
}

