import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaHeart, FaUser, FaShoppingCart, FaSearch, FaMinus, FaPlus } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import './Electronics.css';

function Electronics() {
  const navigate = useNavigate();
  const [products, setProducts] = useState({phones: [], laptops: [], accessories: []});

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/products/');
        const electronics = response.data.electronics;

        const phones = electronics.filter(product => product.name.toLowerCase().includes('iphone') || product.name.toLowerCase().includes('samsung'));
        const laptops = electronics.filter(product => product.name.toLowerCase().includes('macbook') || product.name.toLowerCase().includes('thinkpad') || product.name.toLowerCase().includes('dell'));
        const accessories = electronics.filter(product => !phones.includes(product) && !laptops.includes(product));

        setProducts({phones, laptops, accessories});
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const [cartItems, setCartItems] = useState({});

  const handleCartAction = (productName, action = 'add') => {
    // Logic to add or subtract items from cart
  };

  const renderCartButton = (productName) => {
    // Logic to render cart button with add/subtract functionality
  };

  // Function to render products based on category
  const renderCategory = (category) => (
    <section className="Electronics-section">
      <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
      <div className="product-grid">
        {products[category].map(product => (
          <div className="product-card" key={product.name}>
            <img src={product.url} alt={product.name} />
            <h3>{product.name}</h3>
            <div style={{display: 'flex', marginTop: "10px"}}>
              <p>${product.price}</p>
              {/* Render Cart Button */}
            </div>
          </div>
        ))}
      </div>
    </section>
  );

  return (
    <div className="Electronics">
      {/* Header and Navigation */}
      <main className="Electronics-main">
        {renderCategory('phones')}
        {renderCategory('laptops')}
        {renderCategory('accessories')}
      </main>
      {/* Footer */}
    </div>
  );
}

export default Electronics;
