import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cart.css'; // Import the CSS file for styling
import { useNavigate } from 'react-router-dom';

function Cart() {
  const navigate = useNavigate();
  const handleHome = () => {
    navigate('/Home');
  };

  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [userDetails, setUserDetails] = useState({});

  useEffect(() => {
    // Fetch cart items and user details from API
    const fetchCartItems = async () => {
      const userEmail = localStorage.getItem('user_email');
      if (!userEmail) {
        // Redirect to login or handle unauthorized user
        return;
      }

      try {
        // Fetch cart items info based on user_email
        const response = await axios.post('http://localhost:8000/api/get_cart_items_info/', {
          user_email: userEmail,
        });
        setCartItems(response.data.cart_items);
        setTotalPrice(response.data.total_price);
      } catch (error) {
        console.error('Error fetching cart items:', error);
        // Handle error (e.g., show error message)
      }


      try {
        // Fetch user details based on user_email
        const userDetailsResponse = await axios.post('http://localhost:8000/api/get_user_details/', {
          user_email: userEmail,
        });
        setUserDetails(userDetailsResponse.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
        // Handle error (e.g., show error message)
      }
    };

    fetchCartItems();
  }, []);

  // Function to handle increasing quantity
  const handleIncreaseQuantity = async (product_id, category) => {
    const userEmail = localStorage.getItem('user_email');
    if (!userEmail) {
      // Redirect to login or handle unauthorized user
      return;
    }

    try {
      // Increase quantity for the product in the cart
      await axios.post('http://localhost:8000/api/add_to_cart/', {
        user_email: userEmail,
        category: category,
        product_id: product_id,
        quantity: 1, // Increase by 1
      });
      // Fetch updated cart items and total price after increasing quantity
      fetchCartItems();
    } catch (error) {
      console.error('Error increasing quantity:', error);
      // Handle error (e.g., show error message)
    }
  };

  // Function to handle decreasing quantity
  const handleDecreaseQuantity = async (product_id, category) => {
    const userEmail = localStorage.getItem('user_email');
    if (!userEmail) {
      // Redirect to login or handle unauthorized user
      return;
    }

    try {
      // Decrease quantity for the product in the cart
      await axios.post('http://localhost:8000/api/delete_one_from_cart/', {
        user_email: userEmail,
        category: category,
        product_id: product_id,
      });
      // Fetch updated cart items and total price after decreasing quantity
      fetchCartItems();
    } catch (error) {
      console.error('Error decreasing quantity:', error);
      // Handle error (e.g., show error message)
    }
  };
  return (
    <div className="Cart">
      <div className="back-to-home">
        <button onClick={handleHome}>Back to Home</button>
      </div>
      <div className="user-details">
        <h2 className='heading'>User Details</h2>
        <div className="user-info">
          <div>
            <strong>Name:</strong> {userDetails.name}
          </div>
          <div>
            <strong>Phone Number:</strong> {userDetails.phoneNumber}
          </div>
          <div>
            <strong>Address:</strong> {userDetails.address}
          </div>
        </div>
      </div>
      <h1 className="cart-title">Shopping Cart</h1>
      <div className="cart-items">
        {cartItems.map((item) => (
          <div className="cart-item" key={item.id}>
            <img src={item.src} alt={item.name} />
            <div className="item-details">
              <div className="item-name">{item.name}</div>
              <div className="price-quantity">
                <div className="price">Price: ${item.price}</div>
                <div className="quantity-controls">
                  <button onClick={() => handleDecreaseQuantity(item.id)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => handleIncreaseQuantity(item.id)}>+</button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="bill-section">
        <h2>Bill Details</h2>
        <div className="bill-details">
          <div className="total-items">Total Items: {cartItems.length}</div>
          <div className="total-price">Total Price: ${totalPrice}</div>
          <div className="payment-button">
            <button>Proceed to Payment</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cart;
