import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cart.css'; // Import the CSS file for styling
import { useNavigate } from 'react-router-dom';

function Cart() {
  const navigate = useNavigate();

  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  const fetchCartItems = async () => {
    const userEmail = localStorage.getItem('user_email');
    if (!userEmail) {
      navigate('/login'); // Redirect to login if user email not found
      return;
    }

    try {
      const cartItemsResponse = await axios.post('http://localhost:8000/api/get_cart_items_info', {
        user_email: userEmail,
      });
      const cartItemsData = cartItemsResponse.data.cart_items_info;
      setCartItems(cartItemsData);

      let total = 0;
      cartItemsData.forEach((item) => {
        total += item.price * item.quantity;
      });
      setTotalPrice(total);
    } catch (error) {
      console.error('Error fetching cart items:', error);
    }
  };

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItemQuantities = async (productId, category) => {
    try {
      const userEmail = localStorage.getItem('user_email');
      const response = await axios.post('http://localhost:8000/api/get_cart_quantity/', {
        user_email: userEmail,
        product_id: productId,
        category: category,
      });
      return response.data.quantity;
    } catch (error) {
      console.error('Error fetching cart item quantity:', error);
      return 0; // Return default quantity if fetching fails
    }
  };

  const handleIncreaseQuantity = async (productId, category) => {
    const userEmail = localStorage.getItem('user_email');
    if (!userEmail) {
      navigate('/login');
      return;
    }

    try {
      const currentQuantity = await fetchCartItemQuantities(productId, category);
      const maxQuantity = await getTotalQuantity(productId, category);

      if (currentQuantity === maxQuantity) {
        alert('Max quantity reached. Cannot add more to cart.');
        return;
      }

      await axios.post('http://localhost:8000/api/add_to_cart/', {
        user_email: userEmail,
        category: category,
        product_id: productId,
        quantity: 1,
      });
      fetchCartItems(); // Refresh cart items
    } catch (error) {
      console.error('Error increasing quantity:', error);
    }
  };

  const handleDecreaseQuantity = async (productId, category) => {
    const userEmail = localStorage.getItem('user_email');
    if (!userEmail) {
      navigate('/login');
      return;
    }

    try {
      await axios.post('http://localhost:8000/api/delete_one_from_cart/', {
        user_email: userEmail,
        product_id: productId,
        category: category,
      });
      fetchCartItems(); // Refresh cart items
    } catch (error) {
      console.error('Error decreasing quantity:', error);
    }
  };

  const getTotalQuantity = async (productId, category) => {
    try {
      const response = await axios.post('http://localhost:8000/api/get_product_info', {
        product_id: productId,
        category: category,
      });
      return response.data.quantity;
    } catch (error) {
      console.error('Error fetching total quantity:', error);
      return 0;
    }
  };

  return (
    <div className="Cart">
      {/* Cart items rendering */}
      {cartItems.map((item) => (
        <div className="cart-item" key={item.product_id}>
          <img src={item.url} alt={item.name} />
          <div className="item-details">
            <div className="item-name">{item.name}</div>
            <div className="price-quantity">
              <div className="price">Price: ${item.price}</div>
              <div className="quantity-controls">
                <button onClick={() => handleDecreaseQuantity(item.product_id, item.category)}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleIncreaseQuantity(item.product_id, item.category)}>+</button>
              </div>
            </div>
          </div>
        </div>
      ))}
      {/* Total price and payment button */}
      <div className="bill-section">
        <h2>Bill Details</h2>
        <div className="bill-details">
          <div className="total-items">Total Items: {cartItems.length}</div>
          <div className="total-price">Total Price: ${totalPrice}</div>
          <div className="payment-button">
            <button>Proceed to Payment</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cart;
