import React, { useState, useEffect } from 'react';
import './Home.css'; // Ensure this matches the name of your CSS file
import { FaHeart, FaUser, FaShoppingCart } from 'react-icons/fa';

const DropdownMenu = ({ title, items }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="dropdown">
      <button className="dropbtn">{title}</button>
      {isOpen && (
        <div className="dropdown-content">
          {items.map((item, index) => (
            <a key={index} href={item.path}>{item.label}</a>
          ))}
        </div>
      )}
    </div>
  );
};

const HeroSection = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const slides = [
    { src: 'image1.jpg', text: 'Fast, Nutritious, Plant-based food' },
    // Add more slides
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((currentIndex + 1) % slides.length);
    }, 5000); // Change every 5 seconds

    return () => clearInterval(interval);
  }, [currentIndex, slides.length]);

  return (
    <div className="hero-section">
      <img src={slides[currentIndex].src} alt="Slide" />
      <p>{slides[currentIndex].text}</p>
    </div>
  );
};

function Home() {
  const electronicsMenuItems = [
    { label: 'Smartphones', path: '/electronics/smartphones' },
    { label: 'Laptops', path: '/electronics/laptops' },
    // Add more items
  ];

  return (
    <div className="Home">
      <header className="Home-header">
        <h1 className="logo">ShopNow</h1>
        <div className="header-right">
          <input type="text" placeholder="Search for products" className="search-input" />
          <FaHeart className="icon" />
          <FaUser className="icon" />
          <FaShoppingCart className="icon" />
        </div>
      </header>
      <nav className="Home-nav">
        <DropdownMenu title="Electronics" items={electronicsMenuItems} />
        {/* More menus or links */}
      </nav>
      <HeroSection />
      {/* Additional content sections */}
    </div>
  );
}

export default Home;
