import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaHeart, FaUser, FaShoppingCart, FaSearch, FaMinus, FaPlus } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import './Clothing.css';

function Clothing() {
  const navigate = useNavigate();

  const handleHome = () => {
    navigate('/Home');
  };

  const handleCart = () => {
    navigate('/Cart');
  };

  const handleProfile = () => {
    navigate('/Profile');
  };

  const [clothingItems, setClothingItems] = useState([]);
  const [cartItems, setCartItems] = useState({});

  useEffect(() => {
    const fetchClothing = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/products/');
        setClothingItems(response.data.clothing);
        // Fetch cart quantities for clothing items
        await fetchCartQuantities();
      } catch (error) {
        console.error('Error fetching clothing items:', error);
      }
    };

    fetchClothing();
  }, []);

  const fetchCartQuantities = async () => {
    try {
      const userEmail = localStorage.getItem('user_email');
      const response = await axios.post('http://localhost:8000/api/get_cart_quantity/', {
        user_email: userEmail,
        product_id
      });
      setCartItems(response.data.cart_items);
    } catch (error) {
      console.error('Error fetching cart quantities:', error);
    }
  };


  const addToCart = async (productId) => {
    try {
      await axios.post('http://localhost:8000/api/add_to_cart/', {
        product_id: productId,
        quantity: 1,
      });
      // Refresh cart quantities after adding item
      await fetchCartQuantities();
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  const decreaseQuantity = async (productId) => {
    try {
      await axios.post('http://localhost:8000/api/delete_one_from_cart/', {
        product_id: productId,
      });
      // Refresh cart quantities after deleting item
      await fetchCartQuantities();
    } catch (error) {
      console.error('Error decreasing quantity:', error);
    }
  };

  const renderCartButton = (productId) => {
    const count = cartItems[productId] || 0;
    return count > 0 ? (
      <div style={{ marginLeft: "50px" }} className="quantity-controls">
        <button onClick={() => decreaseQuantity(productId)}><FaMinus /></button>
        <span>{count}</span>
        <button onClick={() => addToCart(productId)}><FaPlus /></button>
      </div>
    ) : (
      <button style={{ marginLeft: "50px" }} className="add-to-cart-button" onClick={() => addToCart(productId)}>
        Add to Cart <FaShoppingCart />
      </button>
    );
  };

  return (
    <div className="Clothing">
      <header className="Clothing-header">
        <h1 className="logo">ShopNow</h1>
        <div className="header-right">
          <div className="search-container">
            <input type="text" placeholder="Search for products" className="search-input" />
            <FaSearch className="search-icon" />
          </div>

          <FaHeart className="icon" />
          <FaUser className="icon" onClick={handleProfile} />
          <FaShoppingCart className="icon" onClick={handleCart} />
        </div>
      </header>
      <nav className="Clothing-nav">
        <ul>
          <li><div onClick={handleHome}>Home</div></li>
          <li><div>Men's</div></li>
          <li><div>Women's</div></li>
          <li><div>Kids'</div></li>
        </ul>
      </nav>
      <main className="Clothing-main">
        {clothingItems.length > 0 && (
          <section className="Clothing-section">
            <h2>All Clothing</h2>
            <div className="product-grid">
              {clothingItems.map(item => (
                <div className="product-card" key={item.id}>
                  <img src={item.url} alt={item.name} />
                  <h3>{item.name}</h3>
                  <div className='addtocartbtn'>
                    <p>${item.price}</p>
                    {renderCartButton(item.id)}
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}
      </main>
      <footer className="Clothing-footer">
        <p>&copy; ShopNow - Built With Love</p>
      </footer>
    </div>
  );
}

export default Clothing;
