import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaHeart, FaUser, FaShoppingCart, FaSearch } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import './Home.css'; // Make sure this path matches your CSS file's location

function Home() {
  const navigate = useNavigate();
  const [products, setProducts] = useState({ electronics: [], decorations: [], clothing: [] });
  const [cartItems, setCartItems] = useState({});
  const user_email = localStorage.getItem('user_email'); // Get user_email from localStorage

  useEffect(() => {
    // Fetch products from the backend when the component mounts
    const fetchProducts = async () => {
      try {
        // Update this URL to match your actual API endpoint
        const response = await axios.get('http://localhost:8000/api/products/');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const fetchCartQuantity = async (productId) => {
    try {
      const response = await axios.get(`http://localhost:8000/api/get_cart_quantity/?user_email=${user_email}&product_id=${productId}`);
      return response.data.quantity || 0;
    } catch (error) {
      console.error('Error fetching cart quantity:', error);
      return 0;
    }
  };

  const addToCart = async (productName, productId) => {
    const currentQuantity = await fetchCartQuantity(productId);
    setCartItems((prevItems) => ({
      ...prevItems,
      [productName]: currentQuantity + 1,
    }));
  };

  const decreaseQuantity = async (productName, productId) => {
    const currentQuantity = await fetchCartQuantity(productId);
    if (currentQuantity > 0) {
      setCartItems((prevItems) => ({
        ...prevItems,
        [productName]: currentQuantity - 1,
      }));
    }
  };

  const renderProducts = (category) => (
    <div className="product-grid">
      {products[category].slice(0, 3).map((product) => (
        <div className="product-card" key={product.name}>
          <img src={product.url} alt={product.name} />
          <h3>{product.name}</h3>
          <div className="priceCart" style={{ display: 'flex', alignItems: 'flex-start' }}>
            <p>${product.price}</p>
            <div className="add-to-cart">
              {cartItems[product.name] !== undefined && cartItems[product.name] > 0 ? (
                <div className="quantity-controls">
                  <button onClick={() => decreaseQuantity(product.name, product.id)}>-</button>
                  <span>{cartItems[product.name]}</span>
                  <button onClick={() => addToCart(product.name, product.id)}>+</button>
                </div>
              ) : (
                <button onClick={() => addToCart(product.name, product.id)}>
                  Add to Cart <FaShoppingCart />
                </button>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div className="Home">
      <header className="Home-header">
        <h1 className="logo">ShopNow</h1>
        <div className="header-right">
          <div className="search-container">
            <input type="text" placeholder="Search for products" className="search-input" />
            <FaSearch className="search-icon" />
          </div>
          <FaHeart className="icon" />
          <FaUser className="icon" onClick={() => navigate('/Profile')} />
          <FaShoppingCart className="icon" onClick={() => navigate('/Cart')} />
        </div>
      </header>
      <nav className="Home-nav" style={{cursor:"pointer"}}>
        <ul>
          <li onClick={() => navigate('/Electronics')}>Electronics</li>
          <li onClick={() => navigate('/Clothing')}>Clothing</li>
          <li onClick={() => navigate('/Decorations')}>Decorations</li>
          {/* Add more categories as needed */}
        </ul>
      </nav>
      <main className="Home-main">
        <section className="Home-section">
          <h2>Featured Electronics</h2>
          {renderProducts('electronics')}
        </section>
        <section className="Home-section">
          <h2>Trending Clothing</h2>
          {renderProducts('clothing')}
        </section>
        <section className="Home-section">
          <h2>Decorations & Home</h2>
          {renderProducts('decorations')}
        </section>
        {/* Additional categories can be added as needed */}
      </main>
      <footer className="Home-footer">
        <p>&copy; ShopNow - Built With Love</p>
      </footer>
    </div>
  );
}

export default Home;
