import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaHeart, FaUser, FaShoppingCart, FaSearch, FaMinus, FaPlus } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import './Clothing.css';

function Clothing() {
  const navigate = useNavigate();
  const handleHome = () => {
    navigate('/Home');
  }
  const handleCart = () => {
    navigate('/Cart');
  }
  const handleProfile = () => {
    navigate('/Profile');
  }

  const [products, setProducts] = useState({ men: [], women: [], kids: [] });

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/products/');
        const clothing = response.data.clothing;

        const men = clothing.filter(product => product.whom.toLowerCase() === 'men');
        const women = clothing.filter(product => product.whom.toLowerCase() === 'women');
        const kids = clothing.filter(product => product.whom.toLowerCase() === 'kid');

        setProducts({ men, women, kids });
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const [cartItems, setCartItems] = useState({});

  const addToCart = (productName) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      [productName]: (prevItems[productName] || 0) + 1,
    }));
  };

  const increaseQuantity = (productName) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      [productName]: prevItems[productName] + 1,
    }));
  };

  const decreaseQuantity = (productName) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      [productName]: Math.max(0, prevItems[productName] - 1),
    }));
  };

  // Helper function to render the add to cart or quantity buttons
  const renderCartButton = (productName) => {
    const count = cartItems[productName] || 0;
    return count > 0 ? (
      <div className="quantity-controls" style={{marginLeft:"40px"}}>
        <button  onClick={() => decreaseQuantity(productName)}><FaMinus /></button>
        <span >{count}</span>
        <button  onClick={() => increaseQuantity(productName)}><FaPlus /></button>
      </div>
    ) : (
      <button style={{marginLeft:"40px"}}  className="add-to-cart-button" onClick={() => addToCart(productName)}>
        Add to Cart <FaShoppingCart />
      </button>
    );
  };

  // Function to render products based on category
  const renderCategory = (category) => (
    <section className="Clothing-section">
      <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
      <div className="product-grid">
        {products[category].map(product => (
          <div className="product-card" key={product.name}>
            <img src={product.url} alt={product.name} />
            <h3>{product.name}</h3>
            <div style={{display: 'flex', marginTop: "10px"}}>
              <p>${product.price}</p>
              <div style={{marginLeft: 'auto'}}>
                {renderCartButton(product.name)}
                </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );

  return (
    <div className="Clothing">
      <header className="Clothing-header">
        <h1 className="logo">ShopNow</h1>
        <div className="header-right">
          <div className="search-container">
            <input type="text" placeholder="Search for products" className="search-input" />
            <FaSearch className="search-icon" />
          </div>

          <FaHeart className="icon" />
          <FaUser className="icon" onClick={handleProfile} />
          <FaShoppingCart className="icon" onClick={handleCart} />
        </div>
      </header>
      <nav className="Clothing-nav">
        <ul>
          <li><div onClick={handleHome}>Home</div></li>
          <li><div>Men's</div></li>
          <li><div>Women's</div></li>
          <li><div>Kids'</div></li>
        </ul>
      </nav>
      <main className="Clothing-main">
        {renderCategory('men')}
        {renderCategory('women')}
        {renderCategory('kids')}
      </main>
      <footer className="Clothing-footer">
        <p>&copy; ShopNow - Built With Love</p>
      </footer>
    </div>
  );
}

export default Clothing;
