import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaHeart, FaUser, FaShoppingCart, FaSearch, FaMinus, FaPlus } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import './Electronics.css';

function Electronics() {
  const navigate = useNavigate();
  const [products, setProducts] = useState({phone: [], laptop: [], accessory: []}); // Separate states for each category

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/products/');
        // Filter and categorize products
        const { electronics } = response.data;
        const categorizedProducts = { phone: [], laptop: [], accessory: [] };
        electronics.forEach(product => {
          if (['phone', 'laptop', 'accessory'].includes(product.what)) {
            categorizedProducts[product.what].push(product);
          }
        });
        setProducts(categorizedProducts);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const [cartItems, setCartItems] = useState({});

  const handleCartAction = (productName, action = 'add') => {
    setCartItems(prevItems => {
      const newQuantity = action === 'add' ? (prevItems[productName] || 0) + 1 : Math.max(0, (prevItems[productName] || 1) - 1);
      if (newQuantity > 0) {
        return { ...prevItems, [productName]: newQuantity };
      } else {
        const { [productName]: remove, ...rest } = prevItems;
        return rest;
      }
    });
  };

  const renderCartButton = (productName) => {
    const count = cartItems[productName] || 0;
    return count > 0 ? (
      <div className="quantity-controls">
        <button onClick={() => handleCartAction(productName, 'subtract')}><FaMinus /></button>
        <span>{count}</span>
        <button onClick={() => handleCartAction(productName, 'add')}><FaPlus /></button>
      </div>
    ) : (
      <button className="add-to-cart-button" onClick={() => handleCartAction(productName, 'add')}>
        Add to Cart <FaShoppingCart />
      </button>
    );
  };

  return (
    <div className="Electronics">
      {/* Header, Navigation, etc. */}
      <main className="Electronics-main">
        {Object.entries(products).map(([category, items]) => (
          <section className="Electronics-section" key={category}>
            <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2> {/* Capitalize the category name */}
            <div className="product-grid">
              {items.map(product => (
                <div className="product-card" key={product.name}>
                  <img src={product.url} alt={product.name} />
                  <h3>{product.name}</h3>
                  <div style={{ display: 'flex', marginTop: "10px" }}>
                    <p>${product.price}</p>
                    {renderCartButton(product.name)}
                  </div>
                </div>
              ))}
            </div>
          </section>
        ))}
      </main>
      <footer className="Electronics-footer">
        <p>&copy; ShopNow - Built With Love</p>
      </footer>
    </div>
  );
}

export default Electronics;
