import React, { useState } from 'react';
import './Home.css'; // Ensure this path matches the location of your CSS file
import { FaHeart, FaUser, FaShoppingCart } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();

  // Navigation handlers
  const handleNavigation = (path) => {
    navigate(`/${path}`);
  };

  // State for cart items
  const [cartItems, setCartItems] = useState({});

  // Add item to cart
  const addToCart = (productName) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      [productName]: prevItems[productName] ? prevItems[productName] + 1 : 1,
    }));
  };

  // Increase item quantity in cart
  const increaseQuantity = (productName) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      [productName]: prevItems[productName] + 1,
    }));
  };

  // Decrease item quantity in cart
  const decreaseQuantity = (productName) => {
    if (cartItems[productName] === 1) {
      const { [productName]: removedItem, ...updatedCart } = cartItems;
      setCartItems(updatedCart);
    } else {
      setCartItems((prevItems) => ({
        ...prevItems,
        [productName]: prevItems[productName] - 1,
      }));
    }
  };

  return (
    <div className="Home">
      <header className="Home-header">
        <h1 className="logo">ShopNow</h1>
        <div className="header-right">
          <input type="text" placeholder="Search for products" className="search-input" />
          <FaHeart className="icon" />
          <FaUser className="icon" onClick={() => handleNavigation('Profile')} />
          <FaShoppingCart className="icon" onClick={() => handleNavigation('Cart')} />
        </div>
      </header>
      <nav className="Home-nav">
        <ul>
          <li><a onClick={() => handleNavigation('Electronics')}>Electronics</a></li>
          <li><a onClick={() => handleNavigation('Clothing')}>Clothing</a></li>
          <li><a>Decorations</a></li>
          <li><a>Men's</a></li>
          <li><a>Women's</a></li>
        </ul>
      </nav>
      <main className="Home-main">
        {/* Sections for Featured Electronics, Trending Clothing, etc. */}
        {/* Ensure to update the placeholders with actual product data and images */}
        {/* Example Section */}
        <section className="Home-section">
          <h2>Featured Section</h2>
          <div className="product-grid">
            {/* Example Product Card */}
            <div className="product-card">
              <img src="your-image-url.jpg" alt="Product Name" />
              <h3>Product Name</h3>
              <p>Product Description</p>
              {/* Add to Cart Button and Quantity Controls */}
              {/* Ensure to replicate this structure for each product */}
            </div>
            {/* More Product Cards */}
          </div>
        </section>
        {/* More Sections */}
      </main>
      <footer className="Home-footer">
        <p>&copy; 2024 ShopNow - All Rights Reserved</p>
      </footer>
    </div>
  );
}

export default Home;
